import { useState, useEffect } from 'react';
import './App.css';
import { getOffersForCompForXDay } from "./api/helper"

function App() {
  //logique JS
  const [offers, setOffers] = useState([])

  useEffect(() => {
    getOffersForCompForXDay({
      minDate: "2022-11-01",
      maxDate: "2022-11-15", 
      compID: 6,
    }).then((data) => {
      setOffers(data)
    })
  },[])

  //le return rend toujours du jsx
  return (
    <div>
        <h1>Il y a {offers.count} offres</h1>
        <button onClick={(e) => console.log(offers)}>Click dessus</button>
    </div>
  );
}

export default App;
