import { useState, useEffect } from 'react';
import './App.css';
import { getOffersForCompForXDay } from "./api/helper"

function App() {
  //logique JS
  const [offers, setOffers] = useState([])
  const dateDebut = "2022-11-01"
  const dateFin = "2022-11-15"


  useEffect(() => {
    getOffersForCompForXDay({
      minDate: dateDebut,
      maxDate: dateFin, 
      compID: 6,
    }).then((data) => {
      setOffers(data)
    })

  },[])
  const offersList = offers.map((offer) => {
    return (
      <div>
        <h1>{offer.coach}</h1>
        <p>{offer.level}</p>
      </div>
    )
  })
  //le return rend toujours du jsx
  return (
    <div>
        <h1>Il y a {offers.count} offres entre le {dateDebut} et le {dateFin}</h1>
        <button onClick={(e) => console.log(offers.results[0])}>Click dessus</button>
        {/* afficher une carte contenant les information de offers si offers existe : */}
        {/* <card>liste des offres : {offersList}</card> */}



    </div>
  );
}

export default App;
