import { useState } from 'react';
import './App.css';
import { getOfferForCompForXDay } from "./api/helper"

function App() {
  //logique JS
  const [count, setCount] = useState(10)

  getOfferForCompForXDay({
    minDate: "2022-11-01",
    maxDate: "", 
    compID: 6,
  })
  //le return rend toujours du jsx
  return (
    <div>
        <h1>Titre avec ma variable count qui vaut: {count}</h1>
        <button onClick={() => setCount((prevValue) => prevValue + 1)}>Click dessus</button>
    </div>
  );
}

export default App;
