import { useState, useEffect } from 'react';
import './App.css';
import { displayOffers, getOffersForCompForXDay } from "./api/helper"
import Calendar from '/Users/bouchafaa/Documents/stage/calendrier/node_modules/react-calendar';
import './App.css';

function App() {
  //logique JS
  const [date, setDate] = useState(new Date());
  const [offers, setOffers] = useState([])
  const dateDebut = "2022-11-01"
  const dateFin = "2022-11-15"


  useEffect(() => {
    getOffersForCompForXDay({
      minDate: dateDebut,
      maxDate: dateFin, 
      compID: 6,
    }).then((data) => {
      setOffers(data.results[1])
    })
  },[])







  //le return rend toujours du jsx
  return (
    <div id="principale">
        <h1>Il y a {offers.count} offres entre le {dateDebut} et le {dateFin}</h1>
        <button>Click dessus</button>
        {/* <div id="displayOffers">{offers?.coach || 0}</div> */}
        <div id="displayOffers">{offers?.coach}</div>

        <div className='app'>
      <h1 className='text-center'>React Calendar</h1>
      <div className='calendar-container'>
        <Calendar onChange={setDate} value={date} />
      </div>
      <p className='text-center'>
        <span className='bold'>Selected Date:</span>{' '}
        {date.toDateString()}
      </p>
    </div>
    </div>

  );
}

export default App;
