import { useState, useEffect } from 'react';
import './App.css';
import { displayOffers, getOffersForCompForXDay } from "./api/helper"
import Calendar from 'react-calendar'
import 'react-calendar/dist/Calendar.css'
import './App.css';



function App() {
  //logique JS
  const [date, setDate] = useState(new Date())
  const [donnes, setDonnes] = useState([])
  const [offers, setOffers] = useState([])
  const [dateDebut, setDateDebut] = useState("2022-11-01")
  const [dateFin, setDateFin] = useState("2022-11-15")


  useEffect(() => {
    getOffersForCompForXDay({
      minDate: dateDebut,
      maxDate: dateFin, 
      compID: 6,
    }).then((data) => {
      setOffers(data)
      console.log(data.results)
      setDonnes(data.results) // je fais ça pour stocker les résultats dans une variable accessible
    })
  },[date])

  const onChange = (date) => {
    setDate(date)
    setDateDebut(date[0].toISOString().slice(0,10))
    setDateFin(date[1].toISOString().slice(0,10)) 
    console.log(date)
  }






  //le return rend toujours du jsx
  return (
    <div id="principale">
        <h1>There are {offers.count} offers between {date[0]?.toDateString()} and {date[1]?.toDateString()}</h1>


        {/* <button>Click dessus</button>
        {/* <div id="displayOffers">{offers?.coach || 0}</div> }
        <div id="displayOffers">{donnes?.coach}</div> */}



    <h1 className='text-center'>React Calendar with Range</h1>
      <div className='calendar-container'>
        <Calendar
          onChange={onChange}
          value={date}
          selectRange={true}
        />
      </div>
      {date.length > 0 ? (
        <p className='text-center'>
          <span className='bold'>Start:</span>{' '}
          {date[0].toDateString()}
          &nbsp;|&nbsp;
          <span className='bold'>End:</span> {date[1].toDateString()}
        </p>
      ) : (
        <p className='text-center'>
          <span className='bold'>Default selected date:</span>{' '}
          {date.toDateString()}
        </p>
      )}

      {offers.count > 0 ? (
      <p className='text-center'>
          je teste si ca marche
        </p>
      ) : (
        <p className='text-center'>
          offer.count est nul
        </p>
      )}









    </div>



  );
}

export default App;
