//On met toutes les fonctions qui permettent de récuperer les données de l'API
const BASE_URL = "https://api.staging.bsport.io"
const API_URL = BASE_URL + "/api/v1/offer/?company="

export function getOffersForCompForXDay({minDate,maxDate,compID=6}){
    //Renvoie les offres pour une compagnie pour un jour donné
    //compID: id de la compagnie
    //day: jour pour lequel on veut les offres
    //return: liste des offres
    const url = `${API_URL}${compID}&min_date=${minDate}&max_date=${maxDate}`
    var data = fetch(url)
    .then(response => response.json())
    .then(data => {
        return data;
    })
    return data;
}

export function displayOffers(offers){
    const card = document.createElement("div")
    card.setAttribute("id","carte")
    card.innerHTML=`
    <div>ca marche</div>
    `
    document.getElementById("displayOffers").appendChild(card)

}
